     req.nh.nlmsg_flags = NLM_F_REQUEST;
     req.nh.nlmsg_type = RTML_NEWLINK;
     req.if.ifi_family = AF_UNSPEC;
     req.if.ifi_index = INTERFACE_INDEX;
     req.if.ifi_change = 0xffffffff; /* ???*/
